# Create the SMTP (Simple Mail Transper Portocol ) Object 

# In[96]:


# Function to attach the images or any file with the html file 
def email_attach_file(mess,filename, extra_headers=None):
    file=open('i.png','rb')
    attachment=MIMEApplication(file.read())
    
    attachment.add_header(
        "Content-Disposition",
        f"attachment; filename= {filename}",
    )
    
    if extra_headers is not None:
        for name, value in extra_headers.items():
            attachment.add_header(name, value)
    mess.attach(attachment)

    file.close()


